mapper.insert:
INSERT /*insertPost*/
INTO post
    (
        title
      , content
      , non_mem_nick
      , non_mem_pwd
      , board_id
      , reg_id
      , written_by_mem
      , reg_date
    )
VALUES
    (
        #{postDTO.title}
      , #{postDTO.content}
      , #{postDTO.nonMemNick}
      , #{postDTO.nonMemPwd}
      , #{postDTO.boardId}
      , #{postDTO.regId}
      , #{postDTO.writtenByMem}
      , NOW()
    )

mapper.update:
UPDATE /*updatePost*/
    post
SET
    title = #{postDTO.title}
  , content = #{postDTO.content}
  , upd_date = NOW()
WHERE
    id = #{postDTO.id}
